
public class Even_num_1to10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		/*
		 * for(int i=1;i<=10;i++) { if(i%2==0) { System.out.println("Even num ::"+i); } }
		 */	
		
		int i;
		i=1;
		while(i<=10)
		{
			if(i%2==0)
			{
			System.out.println("Even number ::  "+i);
			}
			i++;
		}
	}
}
